mapper.select:
select
  examId,exname,  paperId, courseid, majors, grade, term, major, institution, exTime, exPlace, exDate, description, isExam
from cqc_exam.exam


        and examId = #{examid}


        and exname = #{exname}


        and paperId = #{paperid}


        and courseid = #{courseid}


        and stuId = #{stuid}


        and grade = #{grade}


        and term = #{term}


        and major = #{major}


        and institution = #{institution}


        and exTime = #{extime}


        and exPlace = #{explace}


        and exDate = #{exdate}


        and description = #{description}


        and isExam = #{isexam}
